import 'package:flutter/material.dart';import 'package:intl/intl.dart';class DateTimePicker extends StatefulWidget {  @override  _DateTimePickerState createState() => _DateTimePickerState();}class _DateTimePickerState extends State<DateTimePicker> {  DateTime selectedDate;  final DateFormat dateFormat = DateFormat('dd-MM-yyyy HH:mm');  @override  Widget build(BuildContext context) {    return Column(      children: [        Padding(          padding: const EdgeInsets.all(8.0),          child: OutlineButton(            child: Text('Choose Here'),            onPressed: () async {              final selectedDate = await _selectDateTime(context);              if (selectedDate==null) return ;              print(selectedDate);              final selectTime= await _selectTime(context);              if (selectTime==null) return ;              print(selectTime);            },          ),        ),      ],    );  }  Future<DateTime> _selectDateTime(BuildContext context) async {    showDatePicker(context: context,      initialDate: DateTime.now().add(Duration(seconds: 1)),      firstDate: DateTime.now(),      lastDate: DateTime(2021),);  }  Future<TimeOfDay> _selectTime(BuildContext context) async {    final now = DateTime.now();    showTimePicker(context: context,initialTime: TimeOfDay(hour: now.hour, minute: now.minute ));  }}