import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';//import 'orderdata.dart';import 'editlist.dart';class MyOrder extends StatefulWidget {  @override  _MyOrderState createState() => _MyOrderState();}class _MyOrderState extends State<MyOrder> {   List<String>orderList= <String>[ ];  TextEditingController ordercontroller = TextEditingController();  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('List of orders'),        centerTitle: true,),      body:      Column(        children: <Widget>[          Padding(            padding: const EdgeInsets.all(20.0),            child: new TextFormField( controller:ordercontroller ,            decoration: InputDecoration(              labelText: 'enter to make a list',              fillColor: Colors.amber,            ),),          ),        new IconButton(icon: Icon(Icons.add,          size: 24.0,          semanticLabel: 'ADD',          color: Colors.pink,        ), onPressed: (){addItem();}),          new Expanded(            flex: 1,            child: Padding(              padding: const EdgeInsets.all(8.0),              child: new ListView.builder(                  itemCount: orderList.length ,                  itemBuilder: (BuildContext context, int index) =>buildOrderLook(context, index)),            ),          ),        ],      ),    );  }  Widget buildOrderLook(BuildContext context, int index){//    final order =orderList[index];    return  Dismissible(        key: Key(orderList[index]),        child: Card(          child: Padding(            padding: const EdgeInsets.all(8.0),            child: ListTile(              title: Text(orderList[index],style: new TextStyle(fontSize: 15.0),              ),              trailing:Icon(Icons.edit,),              onTap: (){ Navigator.push(context, MaterialPageRoute(builder: (context)=> EditList()));              },            ),          ),        ),      //as we Disscused in meeting => this removeAt function is not working review it      onDismissed: (direction) {          var newList = orderList.removeAt(index);          setState(() {            newList;          });        Scaffold.of(context).showSnackBar(SnackBar(content: Text("Item ${orderList[index]} deleted")));      },    );  }  void addItem() {    setState(() {      orderList.insert(0,ordercontroller.text,);    });  }}