import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'taxi_data.dart';class BookedTaxiListUi extends StatefulWidget {  @override  _BookedTaxiListUiState createState() => _BookedTaxiListUiState();}class _BookedTaxiListUiState extends State<BookedTaxiListUi> {  List<TaxiData> allTaxiData = [    TaxiData(        src: 'bijnor',        des: 'sikhoabad',        date: '12/2/21',        time: '01:13 pm',        status: 'booked'),    TaxiData(        src: 'sikhoabad',        des: 'Delhi',        date: '14/2/21',        time: '10:11 am',        status: 'booked'),  ];  @override  Widget build(BuildContext context) {    return Container(        height: MediaQuery.of(context).size.height * 0.20,        child: ListView.builder(            scrollDirection: Axis.horizontal,            shrinkWrap: true,            itemCount: allTaxiData.length,            itemBuilder: (BuildContext context, int index) =>                buildBookedTexiUi(context, index)));  }  Widget buildBookedTexiUi(BuildContext context, int index) {    return Container(      width: MediaQuery.of(context).size.width * 0.9,      child: GestureDetector(        onTap: () {          Navigator.push(              context,              MaterialPageRoute(                  builder: (BuildContext context) => BookedTaxiListUi()));        },        child: Card(          elevation: 2,          shadowColor: Colors.yellow,          child: Column(            mainAxisAlignment: MainAxisAlignment.start,            crossAxisAlignment: CrossAxisAlignment.start,            children: [              ListTile(                title: Text(                  'Booking Details',                  style: new TextStyle(                    fontSize: 15.0,                    fontWeight: FontWeight.w500,                  ),                ),                trailing: Icon(                  Icons.airline_seat_recline_extra,                ),              ),              Row(                mainAxisAlignment: MainAxisAlignment.spaceEvenly,                children: [                  Text(                    '${allTaxiData[index].src} to ${allTaxiData[index].des}',                    style: new TextStyle(                      fontSize: 20.0,                      fontWeight: FontWeight.bold,                    ),                  ),                  Text(                    '${allTaxiData[index].date} -- ${allTaxiData[index].time}',                    style: new TextStyle(                      fontSize: 15.0,                      fontWeight: FontWeight.bold,                    ),                  ),                ],              ),              Center(                child: Column(//            mainAxisAlignment: MainAxisAlignment.center,//            crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Padding(                      padding: const EdgeInsets.all(8.0),                      child: Text(                        '${allTaxiData[index].status}',                        style: new TextStyle(                            fontSize: 25.0,                            fontWeight: FontWeight.bold,                            color: Colors.green[700]),                      ),                    ),                  ],                ),              ),            ],          ),        ),      ),    );  }}