import 'package:flutter/material.dart';import 'texidata.dart';class BookedTexiListUi extends StatefulWidget {  @override  _BookedTexiListUiState createState() => _BookedTexiListUiState();}class _BookedTexiListUiState extends State<BookedTexiListUi> {  List<String>TexiData= <String>[    'jasndfsafsof','dasdff','efdasdf'  ];  @override  Widget build(BuildContext context) {    return Scaffold(      body: Expanded(        flex: 1,        child: Padding(          padding: const EdgeInsets.all(8.0),          child: new ListView.builder(              itemCount: TexiData.length ,              itemBuilder: (BuildContext context, int index) =>buildBookedTexiUi(context, index)),        ),      ),    );  }  Widget buildBookedTexiUi(BuildContext context, int index) {    return Container(      child: Card(        child: Padding(          padding: const EdgeInsets.all(8.0),          child: ListTile(            title: Text(TexiData[index],style: new TextStyle(fontSize: 15.0),            ),            trailing:Icon(Icons.airline_seat_recline_extra,)          ),        ),      ),    );  }}