import 'package:flutter/material.dart';class HisTexiListUi extends StatefulWidget {  @override  _HisTexiListUiState createState() => _HisTexiListUiState();}class _HisTexiListUiState extends State<HisTexiListUi> {  List<String>TexiData= <String>[    'previous booking 1','previous booking 2','previous booking 3','previous booking 4',  ];  @override  Widget build(BuildContext context) {    return Container(        height: MediaQuery.of(context).size.height *0.20,        child: ListView.builder(                    scrollDirection: Axis.horizontal,                    shrinkWrap: true,                    itemCount: TexiData.length ,                    itemBuilder: (BuildContext context, int index) =>buildBookedTexiUi(context, index)      ));  }  Widget buildBookedTexiUi(BuildContext context, int index) {    return Container(        width: MediaQuery.of(context).size.width * 0.9,        child: Card( elevation: 2, shadowColor: Colors.yellow,      child: ListTile(        title: Text(TexiData[index],style: new TextStyle(fontSize: 15.0),        ),        trailing:Column(          mainAxisAlignment: MainAxisAlignment.end,          crossAxisAlignment: CrossAxisAlignment.end,          children: [            Icon(Icons.airline_seat_recline_extra,),          ],        )      ),    ));  }}