import 'package:flutter/material.dart';import 'package:flutter_week_view/flutter_week_view.dart';class WeekCal extends StatelessWidget {  @override  Widget build(BuildContext context) {    DateTime now = DateTime.now();    DateTime date = DateTime(now.year, now.month, now.day);    return Scaffold(        appBar: AppBar(title: Center(child: Text("weekly"))),        body: WeekView(          dates: [date.subtract(Duration(days: 1)), date, date.add(Duration(days: 1))],          events: [            FlutterWeekViewEvent(              title: 'An event 1',              description: 'work modeeeeee',              start: date.add(Duration(hours: 10)),              end: date.add(Duration(hours: 12)),              backgroundColor: Colors.teal            ),            FlutterWeekViewEvent(              title: 'An event 2',              description: 'nothing more',              start: date.add(Duration(hours: 13)),              end: date.add(Duration(hours: 15)),                backgroundColor: Colors.pinkAccent            ),            FlutterWeekViewEvent(              title: 'An event 3',              description: 'beast',              start: date.add(Duration(hours: 14, minutes: 35)),              end: date.add(Duration(hours: 18),),              backgroundColor: Colors.lime            ),            FlutterWeekViewEvent(                title: 'An event 4',                description: 'sleep mode',                start: date.add(Duration(hours: 23,)),                end: date.add(Duration(hours: 7 , minutes: 30),),                backgroundColor: Colors.black            ),          ],        ),    );  }}