import 'package:flutter/material.dart';import 'package:intl/intl.dart';class DatePicker extends StatefulWidget {  @override  _DatePickerState createState() => _DatePickerState();}class _DatePickerState extends State<DatePicker> {DateTime selectedDate = DateTime.now();  Color _iconColor = Colors.black;  final DateFormat dateFormat = DateFormat('dd-MM-yyyy');  @override  Widget build(BuildContext context) {    return GestureDetector(      child: Row(        mainAxisAlignment: MainAxisAlignment.end,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Padding(            padding: const EdgeInsets.only(left: 11.0, right: 10),            child: Text(              '${dateFormat.format(selectedDate)}',              style: TextStyle(                  color: Colors.green[900],                  fontSize: 20,                  fontWeight: FontWeight.bold),            ),          ),          Icon(            Icons.date_range,            color: _iconColor,          ),        ],      ),      onTap: () => _selectedDate(context),    );  }  Future<Null> _selectedDate(BuildContext context) async {    final DateTime picked = await showDatePicker(context: context, initialDate: selectedDate, firstDate: DateTime.now() , lastDate: DateTime(2021));    if (picked != null && picked != selectedDate)      setState(() {        selectedDate = picked;      });    return null;  }}