import 'package:flutter/material.dart';class TimePicker extends StatefulWidget {  @override  _TimePickerState createState() => _TimePickerState();}class _TimePickerState extends State<TimePicker> {  TimeOfDay selectedTime = TimeOfDay.now();  Color _iconColor = Colors.black;  @override  Widget build(BuildContext context) {    return GestureDetector(      child: Row(        mainAxisAlignment: MainAxisAlignment.end,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Padding(            padding: const EdgeInsets.only(left: 11.0, right: 10),            child: Text(              '${selectedTime.format(context)}',              style: TextStyle(                  color: Colors.green[900],                  fontSize: 20,                  fontWeight: FontWeight.bold),            ),          ),          Icon(              Icons.access_time,              color: _iconColor,            ),        ],      ),      onTap: () => _selectedTime(context),    );  }  Future<Null> _selectedTime(BuildContext context) async {    final TimeOfDay picked = await showTimePicker(        context: context,        initialTime: selectedTime,        builder: (BuildContext context, Widget child) {          return MediaQuery(              data:                  MediaQuery.of(context).copyWith(alwaysUse24HourFormat: false),              child: child);        });    if (picked != null && picked != selectedTime)      setState(() {        selectedTime = picked;      });    return null;  }}